import React from 'react';
import './CurrentTime.css'

class CurrentTime extends React.Component {
    constructor(props){
        super(props);
        this.state = {date: new Date().toLocaleDateString(), time: new Date().toLocaleTimeString(), day: new Date().getDate()};
    }
    componentDidMount() {
        this.timer = setInterval(() => {
            this.setState({date: new Date().toLocaleDateString(), time: new Date().toLocaleTimeString()});
        }, 1000);
    }
    componentWillUnmount(){
        clearInterval(this.timer)
    }
    render() {
        // function trranslate(str) {
        //     const res = str.slice(0, 1);
        //     if res === '下午' {
        //         const afternoon = str.slice(2)
        //     }
        // }
        return (
            <div className='d_and_t'>
                <div className='dates'>Current date: {this.state.date}</div>
                <div className='times'>Current time: {this.state.time}, Current year: {this.state.day}</div>
            </div>

        )
    }
}
export default CurrentTime
