import React from 'react';
import './CurrentTime.css'

class CurrentTime extends React.Component {
    constructor(props){
        super(props);
        this.state = {date: new Date().toLocaleDateString(), time: new Date().toLocaleTimeString(), day: new Date().getDate(), year: new Date().getFullYear(),
        month: new Date().getMonth(), hour: new Date().getHours(), minute: new Date().getMinutes(), second: new Date().getSeconds()};
    }
    componentDidMount() {
        this.timer = setInterval(() => {
            this.setState({date: new Date().toLocaleDateString(), time: new Date().toLocaleTimeString(), day: new Date().getDate(), year: new Date().getFullYear(),
                month: new Date().getMonth(), hour: new Date().getHours(), minute: new Date().getMinutes(), second: new Date().getSeconds()});
        }, 1000);
    }
    componentWillUnmount(){
        clearInterval(this.timer)
    }
    render() {
        // function trranslate(str) {
        //     const res = str.slice(0, 1);
        //     if res === '下午' {
        //         const afternoon = str.slice(2)
        //     }
        // }
        return (
            <div className='d_and_t'>
                <div className='dates'><p className='p1'>{this.state.month} {this.state.day} {this.state.year}</p></div>
                <div className='times'><p className='p2'><span className='s1'>{this.state.hour}</span><span className='c1'>:</span><span className='s2'>{this.state.minute}</span><span className='c2'>:</span><span className='s3'>{this.state.second}</span></p></div>
            </div>

        )
    }
}
export default CurrentTime
